    #[Route('/pokemon-insert-formbuilder', name: 'pokemon_insert_formbuilder')]
    public function insertPokemonFormBuilder(EntityManagerInterface $entityManager, Request $request): Response
    {
        // j ai cree au prealable dans mon terminale en ligne de commande  une classe de "gabarit de formulaire HTML" avec php bin/console make:form, que j ai appelÃ© Pokemon Type qui va etre reliÃ© a mon Entity Pokemon (BDD)

        $pokemon = new Pokemon();
        // j'instancie la classe pokemon


        $pokemonForm = $this->createForm(PokemonType::class, $pokemon);
        // ici je gÃ©nÃ©re une instance de la classe de gabarit de formulaire PokemonType qui va etre reliÃ©e a l'Entity Pokemon (BDD), createForm vient de la classe Parent
        $pokemonForm->handleRequest($request);
        // le handle request permet de lier le formulaire avec la requÃªte, il recupere les donnÃ©es postÃ©es dans la variable (du POST et les stock dans l'Entity)
        if ($pokemonForm->isSubmitted() && $pokemonForm->isValid()) {
            // si le formulaire est submit et qu il est valide alors, le handlerequest sait si cela a Ã©tÃ© postÃ© ou pas
            $entityManager->persist($pokemon); // preparation : on prepare la requete Sql
            $entityManager->flush(); // execute : on execute la requete prÃ©parÃ©e
        }

        return $this->render('Page/pokemon-insert-formbuilder.html.twig', [
            'pokemon' => $pokemon,
            'pokemonForm' => $pokemonForm->createView()


        ]);
        // je retourne une rÃ©ponse HTTP avec le html de pokemonForm

        // on peut retourner autant de variables qu on veut


    }

