    #[Route('/pokemons/update/{id}', name: 'pokemon_update')]
    public function updatePokemon(int $id,PokemonRepository $pokemonRepository, EntityManagerInterface $entityManager, Request $request): Response
    {
        $pokemon = $pokemonRepository->find($id);
        // j'instancie la classe pokemonRepository, pour acceder a des donnÃ©es stockÃ©es on utilise Repository, je demande donc a Symfony d'instancier la classe Repository (dans le controller : ici PokemonController)
        // on aura placer l $id en parametre


        $pokemonUpdateForm = $this->createForm(PokemonType::class, $pokemon);
        // ici je gÃ©nÃ©re une nouvelle instance de la classe de gabarit de formulaire PokemonType (on peut creer plusieurs instances d'une meme classe) qui va etre reliÃ©e a l'Entity Pokemon (BDD), createForm vient de la classe Parent (AbstractController)

        $pokemonUpdateForm->handleRequest($request);
        // le handle request permet de lier le formulaire avec la requÃªte, il recupere les donnÃ©es postÃ©es dans la variable (du POST et les stock dans l'Entity)
        // Pour recuperer une instance de request Symfony vient instancier pour nous, on met request en param

        if ($pokemonUpdateForm->isSubmitted() && $pokemonUpdateForm->isValid()) {
            // si le formulaire est submit et qu il est valide (de type Post) alors, le handlerequest sait si cela a Ã©tÃ© postÃ© ou pas
            $entityManager->persist($pokemon); // preparation : on prepare la requete Sql
            $entityManager->flush(); // execute : on execute la requete prÃ©parÃ©e
        }
        $pokemonUpdateFormView = $pokemonUpdateForm->createView() ;
        //
        return $this->render('Page/pokemon-update-form.html.twig', [
            'pokemon' => $pokemon,
            'pokemonUpdateForm' => $pokemonUpdateFormView
//j envoie en variable twig le formulaire a afficher (deuxieme methode d affichage)

        ]);

    }
